import {useState} from "react";


const EntryForm = ({setEntries} ) => {
	const [title, setTitle] = useState("");
	const [description, setDescription] = useState("");
    let EntryArry = [];

	const createEntry = (e) => {
        e.preventDefault();
		
     console.log({title}, {description});
     //añadirlo al estado del array de entradas -> setEntries
     //llamar funcion
        showEntries({title}, {description})
    };

    const showEntries = (e) => {
        e.preventDefault();
        
        EntryArry.push(e);
    };



	return (
		<form onSubmit={createEntry}>
			<label htmlFor="title"> Title: </label>
			<input id="titile" name="title" value={title} onChange={(e) => {
                setTitle(e.target.value);
            }} />	

            <label htmlFor="description"> Title: </label>
			<input id="description" name="description" value={description} onChange={(e) => {
                setDescription(e.target.value);
            }}/>	
			<button>Create entry</button>
		</form>
		);

    
};

export default EntryForm;
